CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_AgregarItemCarrito`(
    IN p_ProductoId INT, 
    IN p_Nombre VARCHAR(100), 
    IN p_Precio DECIMAL(19,2), 
    IN p_Cantidad INT, 
    IN p_CarritoId INT
)
BEGIN
    DECLARE existente INT DEFAULT 0;
    
    SELECT COUNT(*) INTO existente FROM tbl_carritoitems 
    WHERE ProductoId = p_ProductoId AND CarritoId = p_CarritoId;
    
    IF existente > 0 THEN
        UPDATE tbl_carritoitems 
        SET Cantidad = Cantidad + p_Cantidad 
        WHERE ProductoId = p_ProductoId AND CarritoId = p_CarritoId;
    ELSE
        INSERT INTO tbl_carritoitems (ProductoId, Nombre, Precio, Cantidad, CarritoId) 
        VALUES (p_ProductoId, p_Nombre, p_Precio, p_Cantidad, p_CarritoId);
    END IF;
END ;;
